"use strict";

import {
  checkNotification,
  CLOCK,
  EVENT,
  forceNotification,
  KEYBOARD,
} from "../../src/js/index.js";

const TIMER = {
  BUTTON: {
    START: "START",
    STOP: "STOP",
  },
  DEFAULT: {
    HOURS: "00",
    MINUTES: "25",
    SECONDS: "00",
  },
  ELEMENTS: {
    HOURS: window.document.querySelector(CLOCK.SELECTOR.HOURS),
    MINUTES: window.document.querySelector(CLOCK.SELECTOR.MINUTES),
    SECONDS: window.document.querySelector(CLOCK.SELECTOR.SECONDS),
  },
  INTERVAL: undefined,
};

function handleTimerChange() {
  Object.entries(TIMER.ELEMENTS).forEach(([key, value]) => {
    if (!["HOURS", "MINUTES", "SECONDS"].includes(key)) {
      return;
    }
    value.addEventListener(EVENT.KEY_DOWN, (event) => {
      if (event.key !== KEYBOARD.KEY.TAB) {
        event.preventDefault();
      }
      if (event.key === KEYBOARD.KEY.BACKSPACE || event.key === KEYBOARD.KEY.DELETE) {
        event.target.textContent = "";
      }
      if (!/\d/.test(event.key) || event.target.textContent.length > 1) {
        return;
      }
      event.target.textContent += event.key;
    });
    value.addEventListener(EVENT.BLUR, (event) => {
      event.target.textContent = event.target.textContent.padStart(2, "0");
    });
  });
}

handleTimerChange();

function updateTimer(timer) {
  const { HOURS, MINUTES, SECONDS } = timer;

  if (SECONDS.textContent > 0) {
    SECONDS.textContent = String(SECONDS.textContent - 1).padStart(2, "0");
  } else if (MINUTES.textContent > 0) {
    SECONDS.textContent = "59";
    MINUTES.textContent = String(MINUTES.textContent - 1).padStart(2, "0");
  } else if (HOURS.textContent > 0) {
    SECONDS.textContent = "59";
    MINUTES.textContent = "59";
    HOURS.textContent = String(HOURS.textContent - 1).padStart(2, "0");
  }

  if (SECONDS.textContent === "00" && MINUTES.textContent === "00" && HOURS.textContent === "00") {
    button.textContent = TIMER.BUTTON.START;
    MINUTES.textContent = TIMER.DEFAULT.MINUTES;
    clearInterval(TIMER.INTERVAL);
    forceNotification("Notification", {
      body: "Timer ended",
      icon: "../../../home/images/javascript.png",
    });
    new Audio(
      "data:audio/wav;base64,"
    ).play();
  }
}

const button = window.document.querySelector(".timer-button");
button.addEventListener(EVENT.CLICK, async (_event) => {
  try {
    await checkNotification();
  } catch (error) {
    window.alert("Please, allow notifications to notify you when timer ends");
    return error;
  }

  if (button.textContent === TIMER.BUTTON.START) {
    button.textContent = TIMER.BUTTON.STOP;
    TIMER.INTERVAL = setInterval(
      () => updateTimer(TIMER.ELEMENTS),
      CLOCK.INTERVAL_TIMEOUT_MILLISECONDS
    );
  } else if (button.textContent === TIMER.BUTTON.STOP) {
    button.textContent = TIMER.BUTTON.START;
    clearInterval(TIMER.INTERVAL);
  }
});
